import './Image.css';
import React from 'react';
import { Image } from 'antd';
import { IImageBlock } from 'Document/Block';
import {
	AlignCenterOutlined,
	AlignLeftOutlined,
	AlignRightOutlined,
} from '@ant-design/icons';
import FragmentedString from '@editor/Fragments/FragmentedString';
import {
	createBlockConfigurator,
	createBlockDefinition,
} from 'store/editor/types';
import { FragmentableString } from 'Document/Fragment';
import { getUUID } from 'Document/UUID';

const ImageBlockConfiguratorCenter = createBlockConfigurator({
	blockType: 'Image',
	icon: <AlignCenterOutlined />,
	title: 'Center',
	parameter: 'alignment',
	value: 'center',
});

const ImageBlockConfiguratorLeft = createBlockConfigurator({
	blockType: 'Image',
	icon: <AlignLeftOutlined />,
	title: 'Left',
	parameter: 'alignment',
	value: 'left',
});

const ImageBlockConfiguratorRight = createBlockConfigurator({
	blockType: 'Image',
	icon: <AlignRightOutlined />,
	title: 'Right',
	parameter: 'alignment',
	value: 'right',
});

const alignmentMap: { [key in IImageBlock['config']['alignment']]: string } = {
	left: 'flow-left',
	center: 'flow-down',
	right: 'flow-right',
};

/**
 * Renders a simple image.
 *
 * @param source source of the image to render
 * @param title title of the image rendered at the bottom of the block
 */
const ImageBlock: React.FC<IImageBlock> = ({
	source,
	title,
	config,
	fragmentables,
}) => {
	const { alignment } = config;
	const imageClass = alignmentMap[alignment];

	const titleFragmentable = fragmentables.find((f) => f.id === title);
	return (
		<div
			className={`image-block-container ${imageClass}`}
			style={{ alignItems: alignmentMap[alignment] }}
		>
			<Image
				src={source}
				fallback="data:image/png;base64,"
				preview={false}
				width="100%"
			/>
			{titleFragmentable && (
				<FragmentedString fragmentable={titleFragmentable} />
			)}
		</div>
	);
};

const configurators = [
	ImageBlockConfiguratorLeft,
	ImageBlockConfiguratorCenter,
	ImageBlockConfiguratorRight,
];

const ImageBlockDefinition = createBlockDefinition({
	type: 'Image',
	block: ImageBlock,
	configurators,
	render: (block) => <ImageBlock {...block} />,
	parse: (content, position) => {
		const [source, ...titleText] = content.split('\n');
		const regex =
			/^(?:http(s)?:\/\/)?[\w.-]+(?:\.[\w.-]+)+[\w\-._~:/?#[\]@!$&'()*+,;=.]+$/gm;
		if (!regex.test(source)) {
			const error = new Error(
				`To create an image block please enter a valid url to your image!`
			);
			error.name = 'Malformed input!';
			throw error;
		}

		const title = titleText.join('\n');
		let fragmentable = null;
		if (title) {
			fragmentable = FragmentableString(title);
		}
		return {
			type: 'Image',
			source,
			id: getUUID(),
			fragmentables: fragmentable ? [fragmentable] : [],
			title: fragmentable?.id,
			position,
			config: {
				alignment: 'center',
			},
		};
	},
});

export default ImageBlockDefinition;
